// Erhalten Sie den eingehenden Wert
var eingehenderWert = msg.payload;

// Erstellen Sie ein Date-Objekt für das aktuelle Datum
var aktuellesDatum = new Date();

// Extrahieren Sie Tag, Monat und Jahr
var tag = aktuellesDatum.getDate();
var monat = aktuellesDatum.getMonth() + 1; // Monate werden von 0 bis 11 gezählt, daher +1
var jahr = aktuellesDatum.getFullYear();

// Fügen Sie führende Nullen hinzu, falls erforderlich
if (tag < 10) {
    tag = '0' + tag;
}
if (monat < 10) {
    monat = '0' + monat;
}

// Erstellen Sie das Datum im gewünschten Format (DD-MM-YYYY)
var formatiertesDatum = tag + '-' + monat + '-' + jahr;

// Erstellen Sie das JSON-Objekt mit dem Datum und dem empfangenen Wert
var jsonObjekt = {
    "datum": formatiertesDatum,
    "preis": eingehenderWert
};

// Senden Sie das JSON-Objekt weiter
msg.payload = jsonObjekt;
return msg;

var datum = msg.payload.datum; // Datum aus dem JSON-Objekt
var wert = msg.payload.preis; // Wert aus dem JSON-Objekt

// CSV-Format erstellen
var csvString = datum + ',' + wert;

msg.payload = csvString; // Das CSV-Format als Payload setzen
return msg;

// Überprüfen, ob die Nachricht ein gültiges JSON-Objekt enthält
if (msg.payload && typeof msg.payload === 'object' && msg.payload.Datum && msg.payload.Preis) {
    // Extrahiere Datum und Preis aus dem JSON-Objekt
    const datumParts = msg.payload.Datum.split('-'); // Splitte das Datum in Tag, Monat und Jahr
    const tag = parseInt(datumParts[0], 10);
    const monat = parseInt(datumParts[1], 10) - 1; // Monate in JavaScript sind 0-basiert
    const jahr = parseInt(datumParts[2], 10);

    // Erstelle ein neues Datumobjekt
    const datumObjekt = new Date(jahr, monat, tag);

    // Ändere msg.timestamp zu dem angegebenen Datum
    msg.timestamp = datumObjekt.getTime(); // Hier wird das Datum in Millisekunden seit dem 1. Januar 1970 (Unix-Zeit) umgewandelt

    // Setze msg.payload auf den Preis
    msg.payload = msg.payload.Preis;

    // Fertige Nachricht senden
    return msg;
} else {
    // Wenn die Nachricht nicht das erwartete Format hat, logge einen Fehler und sende die unveränderte Nachricht
    node.error("Ungültiges Nachrichtenformat. Erwarte {\"Datum\":\"07-04-2024\",\"Preis\":397}");
    return null;
}

